import React, { useState, useEffect } from 'react';
import Treadmill from '../activities/treadmill';

export function Workout() {
    const [treadmillToggle, setTreadmillToggle] = useState(true);
    const [benchPressToggle, setBenchPressToggle] = useState(false);
    const [legPressToggle, setLegPressToggle] = useState(false);

    useEffect(() => {
        console.log(treadmillToggle);
        console.log(benchPressToggle);
        console.log(legPressToggle);
        console.log("\n")
    }, [treadmillToggle, benchPressToggle, legPressToggle]);

    return (
        <div>
            <select name="exercises" value>
                <option value={treadmillToggle}>
                    treadmill
                </option>
                <option value={benchPressToggle} > bench press </option>
                <option value={legPressToggle} >
                    leg press
                </option>
            </select>

            {treadmillToggle && <Treadmill />}
        </div>
    );
}

export default Workout;
